settings.liquid:
{% assign has_stock = false %}
{% assign title_included = false %}
{% assign is_na_zalogi = false %}

{% for variant in product.variants %}
  {% if variant.inventory_quantity > 0 %}
    {% assign has_stock = true %}
    {% break %}
  {% endif %}
{% endfor %}

{% if product.tags contains 'Na zalogi' or product.tags contains 'na zalogi' %}
  {% assign is_na_zalogi = true %}
{% endif %}

{% if product.title contains 'ARGI GAS' or
        product.title contains 'AS SCHWABE' or
        product.title contains 'ASSIST' or
        product.title contains 'CELLFAST' or
        product.title contains 'KUBALA' or
        product.title contains 'POPAR' or
        product.title contains 'RUKO' or
        product.title contains 'TERRAX' or
        product.title contains 'UNIOR' or
        product.title contains 'Unior' or
        product.title contains 'UVOZ' or
        product.title contains 'WEILER' %}
  {% assign title_included = true %}
{% endif %}

{% if product.available and has_stock %}
  {% if is_na_zalogi %}
    <div class="tooltip">
      <svg focusable="false" class="icon icon--store-availability-in-stock" viewBox="0 0 18 14" role="presentation">
        <path d="M2 6l5 5 9-9" stroke="#00C200" stroke-width="3" fill="none"></path>
      </svg>
      <span>Na zalogi v naši <span class="zelena"> poslovalnici</span></span>
