    <div class="tooltip">
      <svg focusable="false" class="icon icon--store-availability-in-stock" viewBox="0 0 18 14" role="presentation">
        <path d="M2 6l5 5 9-9" stroke="#00C200" stroke-width="3" fill="none"></path>
      </svg>
      <span> 
      Pri vas<span class="zelena"> 1-2 delovnih dni</span>
      <span class="tooltiptext">Običajno je izdelek poslan isti ali naslednji dan.
    </span></span>
    </div>
  {% elsif title_included %}
    <div class="tooltip">
      <svg focusable="false" class="icon icon--store-availability-in-stock" viewBox="0 0 18 14" role="presentation">
        <path d="M2 6l5 5 9-9" stroke="#00C200" stroke-width="3" fill="none"></path>
      </svg>
      <span>Na voljo preko spleta</span>
      <p> 
        Pri vas <span class="zelena"> 2-5 delovnih dni</span></p>
    </div>
  {% else %}
    <div class="tooltip">
      <svg focusable="false" class="icon icon--store-availability-in-stock" viewBox="0 0 18 14" role="presentation">
        <path d="M2 6l5 5 9-9" stroke="#00C200" stroke-width="3" fill="none"></path>
      </svg>
      <span>Na zalogi</span>
      <span class="tooltiptext">Običajno je izdelek poslan isti ali naslednji dan.
      </span>
    </div>

  {% endif %}
{% else %}
  {% if product.title contains 'Unior' or product.title contains 'UNIOR' %}
    <svg focusable="false" class="icon icon--store-availability-in-stock" viewBox="0 0 18 14" role="presentation">
      <path d="M2 6l5 5 9-9" stroke="#00C200" stroke-width="3" fill="none"></path>
    </svg>
    Rok dobave od 7-14dni
  {% else %}
    <svg focusable="false" class="icon icon--store-availability-in-stock" viewBox="0 0 18 14" role="presentation">
      <path d="M2 6l5 5 9-9" stroke="#00C200" stroke-width="3" fill="none"></path>
    </svg>
    Po naročilu
  {% endif %}
{% endif %}

